                $("#id_per_juridicaP").removeClass("per_juridicaP");
                $("#id_per_juridicaP").addClass("per_juridica_checkP");
                $("#id_per_juridica").removeClass("per_juridica");
                $("#id_per_juridica").addClass("per_juridica_check");
