            else if (personaCheck == "jur" && $("#rdUsertype0").is(":checked")) {
                personaCheck = "nat";
                var tamanoP = document.documentElement.clientWidth;
                if (tamanoP < 992) {
                    $("#botonesSTPN2").removeClass("stpjS2");
                    $("#botonesSTPN2").addClass("stpnS2");
